-- Enable Extensions
CREATE EXTENSION IF NOT EXISTS postgis WITH SCHEMA extensions;

-- Create Tables
CREATE TABLE IF NOT EXISTS public.companies (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    name TEXT NOT NULL,
    description TEXT,
    siret TEXT UNIQUE,
    email TEXT NOT NULL UNIQUE,
    owner_id UUID REFERENCES auth.users(id) ON DELETE SET NULL,
    available_licenses INTEGER DEFAULT 0 NOT NULL,
    contact_preference BOOLEAN DEFAULT true,
    institution_settings JSONB,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

CREATE TABLE IF NOT EXISTS public.profiles (
    id UUID REFERENCES auth.users(id) ON DELETE CASCADE PRIMARY KEY,
    first_name TEXT,
    last_name TEXT,
    age INTEGER,
    gender TEXT,
    department TEXT,
    school TEXT,
    phone_number TEXT,
    avatar TEXT DEFAULT 'light',
    accent_color TEXT DEFAULT 'orange',
    home_preference TEXT DEFAULT 'questionnaire',
    company_id BIGINT REFERENCES public.companies(id) ON DELETE SET NULL,
    license_link_id BIGINT,
    license_active BOOLEAN DEFAULT false,
    contact_preference BOOLEAN DEFAULT true,
    is_recruiter BOOLEAN DEFAULT false,
    institution_data JSONB,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    avatar_json JSONB
);

ALTER TABLE public.profiles
    ADD COLUMN IF NOT EXISTS has_paid BOOLEAN DEFAULT false,
    ADD COLUMN IF NOT EXISTS stripe_customer_id TEXT,
    ADD COLUMN IF NOT EXISTS stripe_last_checkout_id TEXT,
    ADD COLUMN IF NOT EXISTS stripe_last_payment_at TIMESTAMP WITH TIME ZONE;

CREATE TABLE IF NOT EXISTS public.class_groups (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    name TEXT NOT NULL,
    description TEXT,
    company_id BIGINT REFERENCES public.companies(id) ON DELETE CASCADE NOT NULL,
    is_active BOOLEAN DEFAULT true,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

CREATE TABLE IF NOT EXISTS public.license_links (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    company_id BIGINT REFERENCES public.companies(id) ON DELETE CASCADE NOT NULL,
    class_group_id BIGINT REFERENCES public.class_groups(id) ON DELETE SET NULL,
    token TEXT UNIQUE NOT NULL,
    description TEXT,
    max_uses INTEGER DEFAULT 1,
    used_count INTEGER DEFAULT 0,
    expires_at TIMESTAMP WITH TIME ZONE,
    is_active BOOLEAN DEFAULT true,
    custom_branding JSONB,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

ALTER TABLE public.profiles ADD CONSTRAINT fk_license_link_id FOREIGN KEY (license_link_id) REFERENCES public.license_links(id) ON DELETE SET NULL;

CREATE TABLE IF NOT EXISTS public.questions (
    id BIGINT PRIMARY KEY,
    content TEXT NOT NULL,
    category TEXT NOT NULL,
    questionnaire_type TEXT NOT NULL,
    interpretations JSONB NOT NULL
);

CREATE TABLE IF NOT EXISTS public.user_responses (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
    question_id BIGINT REFERENCES public.questions(id) ON DELETE CASCADE NOT NULL,
    response TEXT NOT NULL,
    questionnaire_type TEXT NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

CREATE TABLE IF NOT EXISTS public.user_results (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
    questionnaire_type TEXT NOT NULL,
    personality_analysis TEXT,
    skills_assessment TEXT,
    job_recommendations JSONB,
    study_recommendations JSONB,
    formation_recommendations JSONB,
    skills_data JSONB,
    share_image_url TEXT,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
