-- Create RLS Policies
CREATE POLICY "Users can view their own profile" ON public.profiles FOR SELECT USING (auth.uid() = id);
CREATE POLICY "Users can insert their own profile" ON public.profiles FOR INSERT WITH CHECK (auth.uid() = id);
CREATE POLICY "Users can update their own profile" ON public.profiles FOR UPDATE USING (auth.uid() = id);

CREATE POLICY "Users can manage their own progression" ON public.user_progression FOR ALL USING (auth.uid() = user_id);

CREATE POLICY "Authenticated users can view company data" ON public.companies FOR SELECT USING (auth.role() = 'authenticated');
CREATE POLICY "Company owners can insert companies" ON public.companies FOR INSERT WITH CHECK (auth.uid() = owner_id);
CREATE POLICY "Company owners can update their own company" ON public.companies FOR UPDATE USING (auth.uid() = owner_id);

CREATE POLICY "Authenticated users can view class groups" ON public.class_groups FOR SELECT USING (auth.role() = 'authenticated');
CREATE POLICY "Company members can manage class groups" ON public.class_groups FOR ALL USING (
    (SELECT company.owner_id FROM public.companies AS company WHERE company.id = class_groups.company_id) = auth.uid()
);

CREATE POLICY "Authenticated users can view license links" ON public.license_links FOR SELECT USING (auth.role() = 'authenticated');
CREATE POLICY "Company members can manage license links" ON public.license_links FOR ALL USING (
    (SELECT company.owner_id FROM public.companies AS company WHERE company.id = license_links.company_id) = auth.uid()
);

CREATE POLICY "Authenticated users can view questions" ON public.questions FOR SELECT USING (auth.role() = 'authenticated');

CREATE POLICY "Users can manage their own responses" ON public.user_responses FOR ALL USING (auth.uid() = user_id);
CREATE POLICY "Users can manage their own results" ON public.user_results FOR ALL USING (auth.uid() = user_id);
CREATE POLICY "Users can manage their own favorite formations" ON public.user_favorite_formations FOR ALL USING (auth.uid() = user_id);
CREATE POLICY "Users can manage their own messages" ON public.messages FOR ALL USING (auth.uid() = sender_id OR auth.uid() = recipient_id);
CREATE POLICY "Anyone can read global chat" ON public.global_chat FOR SELECT USING (true);
CREATE POLICY "Authenticated users can insert their messages" ON public.global_chat FOR INSERT WITH CHECK (auth.uid() = user_id);

CREATE POLICY "Company members can view their invoices" ON public.invoices FOR SELECT USING (
    (SELECT company.owner_id FROM public.companies AS company WHERE company.id = invoices.company_id) = auth.uid()
);

CREATE POLICY "Authenticated users can view custom formations" ON public.custom_formations FOR SELECT USING (auth.role() = 'authenticated');
CREATE POLICY "Company members can manage their custom formations" ON public.custom_formations FOR ALL USING (
    (SELECT company.owner_id FROM public.companies AS company WHERE company.id = custom_formations.company_id) = auth.uid()
);

CREATE POLICY "All users can view public formation and job data" ON public.formation_france FOR SELECT USING (true);
CREATE POLICY "All users can view public formation and job data" ON public.metiers_france FOR SELECT USING (true);

-- Create Functions and Triggers
CREATE OR REPLACE FUNCTION public.handle_new_user()
RETURNS TRIGGER AS $$
DECLARE
  avatar_data JSONB;
  questionnaire_data JSONB;
BEGIN
  -- Extract avatar and questionnaire data from user metadata
  avatar_data := new.raw_user_meta_data->'avatarData';
  questionnaire_data := new.raw_user_meta_data->'questionnaireResponses';
  
  -- Insert profile with avatar data
  INSERT INTO public.profiles (
    id, 
    first_name, 
    last_name, 
    age, 
    gender,
    department,
    school,
    phone_number,
    avatar,
    avatar_json
  )
  VALUES (
    new.id,
    COALESCE(new.raw_user_meta_data->>'first_name', new.raw_user_meta_data->>'prenom', ''),
    COALESCE(new.raw_user_meta_data->>'last_name', new.raw_user_meta_data->>'nom', ''),
    COALESCE((new.raw_user_meta_data->>'age')::INTEGER, 18),
    COALESCE(new.raw_user_meta_data->>'gender', new.raw_user_meta_data->>'genre', ''),
    COALESCE(new.raw_user_meta_data->>'department', new.raw_user_meta_data->>'departement', ''),
    COALESCE(new.raw_user_meta_data->>'school', new.raw_user_meta_data->>'ecole', ''),
    COALESCE(new.raw_user_meta_data->>'phone_number', new.raw_user_meta_data->>'numero_telephone', new.raw_user_meta_data->>'numeroTelephone', ''),
    COALESCE(avatar_data->>'url', avatar_data->>'provider', 'dicebear'),
    avatar_data
  );
  
  -- Insert questionnaire responses if provided
  IF questionnaire_data IS NOT NULL AND jsonb_typeof(questionnaire_data) = 'array' AND jsonb_array_length(questionnaire_data) > 0 THEN
    INSERT INTO public.user_responses (user_id, question_id, response, questionnaire_type)
    SELECT 
      new.id,
      (item->>'question_id')::BIGINT,
      item->>'answer',
      'inscription'
    FROM jsonb_array_elements(questionnaire_data) AS item
    WHERE (item->>'question_id') IS NOT NULL 
      AND (item->>'answer') IS NOT NULL
      AND (item->>'question_id')::TEXT ~ '^[0-9]+$';
  END IF;
  
  RETURN new;
EXCEPTION WHEN OTHERS THEN
  -- If anything fails, still allow user creation but log the error
  RAISE NOTICE 'Error in handle_new_user: %', SQLERRM;
  RETURN new;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;
CREATE TRIGGER on_auth_user_created
  AFTER INSERT ON auth.users
  FOR EACH ROW EXECUTE PROCEDURE public.handle_new_user();

CREATE OR REPLACE FUNCTION public.update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
   NEW.updated_at = NOW();
   RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER update_companies_updated_at BEFORE UPDATE ON public.companies FOR EACH ROW EXECUTE FUNCTION public.update_updated_at_column();
CREATE TRIGGER update_profiles_updated_at BEFORE UPDATE ON public.profiles FOR EACH ROW EXECUTE FUNCTION public.update_updated_at_column();
CREATE TRIGGER update_user_results_updated_at BEFORE UPDATE ON public.user_results FOR EACH ROW EXECUTE FUNCTION public.update_updated_at_column();

